'create or replace PROCEDURE enqueue_message(p_message IN CLOB) IS',
'    enqueue_options    dbms_aq.enqueue_options_t;',
'    message_properties dbms_aq.message_properties_t;',
'    message_handle     RAW(16);',
'    message            JSON;',
'BEGIN',
'    -- Convert the input CLOB to a JSON object',
'    message := JSON(p_message);',
'',
'    -- Set the consumer name',
'    message_properties.correlation := ''my_subscriber_json'';',
'',
'    -- Enqueue the message',
'    dbms_aq.enqueue(',
'        queue_name           => ''XX_TANGO.pm_teq_json'',           ',
'        enqueue_options      => enqueue_options,       ',
'        message_properties   => message_properties,     ',
'        payload              => message,               ',
'        msgid                => message_handle',
'    );',
'',
'    -- Output the serialized JSON message for debugging',
'    dbms_output.put_line(json_serialize(message));',
'',
'    -- Commit the transaction',
'    COMMIT;',
'EXCEPTION',
'    WHEN OTHERS THEN',
'        dbms_output.put_line(''Error: '' || SQLERRM);',
'        RAISE;',
'END enqueue_message;'))
,p_required_patch=>wwv_flow_imp.id(9220349343546944)
