'    message_properties.correlation := ''my_subscriber_json'';',
'',
'    -- Enqueue the message',
'    dbms_aq.enqueue(',
'        queue_name           => ''XX_TANGO.pm_teq_json'',           ',
'        enqueue_options      => enqueue_options,       ',
'        message_properties   => message_properties,     ',
